package in.Operators;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		a+=10; //a=a+10
		System.out.println(a);
		
		a-=10; //a=a-10
		System.out.println(a);
		
		a*=20; //a=a*20
		System.out.println(a);
		
		a/=10; //a=a/10
		System.out.println(a);
		
		a%=30;  //a=a%3o0
		System.out.println(a);
	}

}
