package in.Operators;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20,c=10;
		System.out.println("(a>b)&&(a==c)= "+((a>b)&&(a==c)));
		System.out.println("(a>b)||(a==c)= "+((a>b)||(a==c)));
		System.out.println("!(a>b)= "+(!(a>b)));
		
	}

}
